$(document).ready(function() {
    var swiper = new Swiper('.swiper-container', {
        speed: 600,
        // parallax: true,
        preventClicks: false,
        preventClicksPropagation: false,
        pagination: {
            el: '.swiper-pagination',
            clickable: true,
        },
        navigation: {
            nextEl: '.swiper-button-next',
            prevEl: '.swiper-button-prev',
        },
    });
});